import React from 'react'
import './CoursesTeach.css'

const CoursesTeach = (props) => {


  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '',
    number_of_students: ''
});

const handleChange = event => {
    const { name, value, type, checked } = event.target;
    setFormData(prevFormData => ({
      ...prevFormData,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  function handleSubmit(event){
    event.preventDefault()
    Axios.post('http://localhost:3001/api/insert/mainDish',{
      title: formData.name,
      description: formData.description,
      price: formData.price,
      number_of_students: formData.number_of_students
    }).then(()=>{
        console.log(formData)
    })
};

function handleDelete(title){
    Axios.delete(`http://localhost:3001/api/delete/${title}`)
  }

  return (
    <div className="courses-body_t">
      <div className="first-col">
        <div className="first-row">Предмет</div>
        <div className="second-row">Математика</div>
      </div>
      <div className="second-col">
        <div className="first-row">Описание</div>
        <p className="second-row">Математика для чайников и сковародок
        от А до + бесконечности</p>
      </div>
      <div className="third-col">
        <div className="first-row">25.03.2023</div>
        <button onClick={props.toggleShown} className="second-row_t_c">{props.isShown ? "Отменить запись" : "Записаться"}</button>
      </div>
    </div>
  )
}

export default CoursesTeach
