import React from 'react'
import Calendar from './Calendar/CalendarTeach'
import ContactInfoTeach from './ContactInfo/ContactInfoTeach'
import CoursesTeach from './Courses/CoursesTeach'
import PersonalInfoTeach from './PersonalInfo Teacher/PersonalInfoTeach'
import './teacherProfile.css'
import Sidebar from '../Sidebar/Sidebar'
import Axios from 'axios'
import { useNavigate } from 'react-router-dom'


const TeacherProfile = (props) => {
  const [isShown, setIsShown] = React.useState(false)

  const [mainCourseList, setMainCourseList] = React.useState([])

  function toggleShown(){
    setIsShown(prevShown => !prevShown)
  }

  let navigate = useNavigate()
  const routeHandler = (URL) => {
    navigate(URL)
  }

  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '',
    number_of_students: ''
});

const handleChange = event => {
    const { name, value, type, checked } = event.target;
    setFormData(prevFormData => ({
      ...prevFormData,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  function handleSubmit(event){
    event.preventDefault()
    Axios.post('http://localhost:3001/api/insert/mainDish',{
      title: formData.name,
      description: formData.description,
      price: formData.price,
      number_of_students: formData.number_of_students
    }).then(()=>{
        console.log(formData)
    })
};

function handleDelete(title){
    Axios.delete(`http://localhost:3001/api/delete/${title}`)
  }

  const CourseListArr = mainCourseList.map(course=>{
    return <CoursesTeach
      key={course.id}
      name={course.name}
      desc={course.desc}
      price={course.price}
      num_of_stud={course.num_of_students}
      {...course}/>
  })

      React.useEffect(()=>{
      Axios.get('http://localhost:3001/api/get/mainDish').then((response)=>{
          setMainCourseList(response.data)
      })
    }, [])

  return (
    <div className="main">
      <Sidebar/>
      <div className='settings-block_t'>
        <PersonalInfoTeach/>
        <ContactInfoTeach/>
        <h1 className="profile-title_t">Актуальные курсы</h1>
        <section className='course--list'>{CourseListArr}</section>
        {/* <Calendar /> */}
        <CoursesTeach />
        <div onClick={() => routeHandler('/editTeach')} className="edit">Редактировать профиль</div>

    </div>
    </div>
  )
}

export default TeacherProfile
