import React from 'react'
import './CoursesTeach.css'
import Axios from 'axios'
import { useState } from 'react';

const EditCoursesTeach = (props) => {

    const [formData, setFormData] = useState({
        subj_name: '',
        description: ''
    });

    const handleChange = event => {
        const { name, value, type, checked } = event.target;
        setFormData(prevFormData => ({
          ...prevFormData,
          [name]: type === 'checkbox' ? checked : value
        }));
      };

      function handleSubmit(event){
        event.preventDefault()
        Axios.post('http://localhost:3001/api/insert/mainDish',{
          title: formData.subj_name,
          description: formData.description,
        }).then(()=>{
            console.log(formData)
        })
    };

    function handleDelete(title){
        Axios.delete(`http://localhost:3001/api/delete/${title}`)
      }

  return (
    <div className="courses-body_t">
      <div className="first-col">
        <div className="first-row">Предмет</div>
        <input
            type="text"
            placeholder="Название предмета"
            name="subj_name"
            className="second-row_e"
            value={formData.subj_name}
            onChange={handleChange} />
      </div>
      <div className="second-col">
        <div className="first-row">Описание</div>
        <input
            type="text"
            placeholder="Описание курса"
            name="description"
            className="second-row_e"
            value={formData.description}
            onChange={handleChange} />
      </div>
      <div className="third-col">
        <div className="first-row">25.03.2023</div>
        <button onClick={handleSubmit} className="second-row_t_c">Сохранить</button>
        <button onClick={handleDelete} className="second-row_t_c">Удалить</button>
      </div>
    </div>
  )
}

export default EditCoursesTeach
