import React from 'react'
import './CoursesTeach.css'
import Axios from 'axios'
import { useState } from 'react';
import axiosInstance from '../../../axios';

const EditCoursesTeach = (props) => {

    const [formData, setFormData] = useState({
        name: '',
        description: '',
        price: '',
        number_of_students: ''
    });

    const handleChange = event => {
        const { name, value, type, checked } = event.target;
        setFormData(prevFormData => ({
          ...prevFormData,
          [name]: type === 'checkbox' ? checked : value
        }));
      };

      function handleSubmit(event){
        event.preventDefault()
        axiosInstance.post(`/update/course/`,{
          title: formData.name,
          description: formData.description,
          price: formData.price,
          number_of_students: formData.number_of_students
        }).then(()=>{
            console.log(formData)
        })
    };

    function handleDelete(id){
        axiosInstance.delete(`/delete/course/${id}`)
      }

  return (
    <div className="courses-body_t">
      <div className="first-col">
        <div className="first-row">Предмет</div>
        <input
            type="text"
            placeholder="Название предмета"
            name="name"
            className="second-row_e"
            value={props.name}
            onChange={handleChange} />
            <input
            type="text"
            placeholder="Цена"
            name="price"
            className="second-row_e"
            value={props.price}
            onChange={handleChange} />
      </div>
      <div className="second-col">
        <div className="first-row">Описание</div>
        <input
            type="text"
            placeholder="Описание курса"
            name="description"
            className="second-row_e"
            value={props.description}
            onChange={handleChange} />
               <input
            type="text"
            placeholder="Количество студентов"
            name="number_of_students"
            className="second-row_e"
            value={props.number_of_students}
            onChange={handleChange} />
      </div>
      <div className="third-col">
        <div className="first-row">Количество мест</div>
        <input
            type="text"
            placeholder=" Колич. учеников"
            name="number_of_students"
            className="second-row_e"
            value={props.number_of_students}
            onChange={handleChange} />

        <button onClick={handleSubmit} className="second-row_t_c">Сохранить</button>
        <button  onClick={()=>{handleDelete(props.id)}} className="second-row_t_c">Удалить</button>
      </div>
    </div>
  )
}

export default EditCoursesTeach
