import './ContactInfo.scss'
import { useState } from 'react';


const ContactInfoStudEdit = () => {

    const [formData, setFormData] = useState({
        email: '',
        phone: '',
        address: ''
      });

      const handleChange = event => {
        const { name, value, type, checked } = event.target;
        setFormData(prevFormData => ({
          ...prevFormData,
          [name]: type === 'checkbox' ? checked : value
        }));
      };

  return (
    <div className="contact-body">
      <div className="first-col">
        <div className="first-row">Номер телефона</div>
        <div className="second-row">Эл. почта</div>
        <div className="third-row">Домашний адрес</div>
      </div>
        <div className="second-col">
            <input
                type="text"
                placeholder="Номер Телефона"
                name="phone"
                className="form--input"
                value={formData.phone}
                onChange={handleChange}
            />
            <input
                type="email"
                placeholder="Email адрес"
                name="email"
                className="form--input"
                value={formData.email}
                onChange={handleChange}
            />
            <input
                type="text"
                placeholder="Домашний адрес"
                name="address"
                className="form--input"
                value={formData.address}
                onChange={handleChange} />
        </div>
    </div>
  )
}

export default ContactInfoStudEdit
