import Search from '../Search/Search'
import './StudentProfile.scss'
import Sidebar from '../Sidebar/Sidebar'
import Dashboard from '../Dashboard/Dashboard'
import React from 'react'
import PersonalInfoEdit from './PersonalInfo/PersInfoEdit'
import ContactInfoStudEdit from './ContactInfo/ContactStudEdit'
import axiosInstance from '../../axios'

const StudentEdit = () => {


  const [mainStudList, setMainStudList] = React.useState([])

  React.useEffect(() => {
    axiosInstance
      .get("curr/")
      .then((response) => {
        const mainTeacherData = response.data[0];
        setMainStudList(mainTeacherData);

        console.log("student data", mainTeacherData);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);

  return (
    <div className="main">
      <Sidebar/>
      <div className="profile-wrapper">
      {/* <Search /> */}
      <h1 className="profile-title">Настройки профиля ученика</h1>
      <div className="settings-block">
        <PersonalInfoEdit
            id={mainStudList.id}
           first_name={mainStudList.first_name}
           last_name={mainStudList.last_name}
           birth_date={mainStudList.birth_date}
        />
        <ContactInfoStudEdit
          id={mainStudList.id}
          email={mainStudList.email}
          phone={mainStudList?.student?.phone}
        />
        {/* <div className="edit">Сохранить изминения</div> */}
      </div>
    </div>
    {/* <Dashboard/> */}
    </div>
  )
}

export default StudentEdit
